def count(handle)
  # Compte le nombre de journalistes dans le tableau
  puts "Il y a #{handle.size} journalistes dans le tableau"
end

def has_digits?(str)
  # On verifie si il y un chiffre
  str.count("0-9") > 0
end

def contain_number(handle)
  # Affiche le nombre de handle contenant un chiffre
  sub_handle = []
  handle.each do |str|
    if has_digits?(str)
      sub_handle << str
    end
  end
  puts "Il y a #{sub_handle.size} contenant un chiffre"
end

def contain_Aude(handle)
  #combien de handle contiennent la string "Aude"
  sub_handle = []
  handle.each do |str|
    if str.match(/[Aa][Uu][Dd][Ee]/)
      sub_handle << str
    end
  end
  puts "Il y a #{sub_handle.size} handle qui contiennent Aude"
end

def start_uppercase(handle)
  #combien de handle contiennent une majuscule en première lettre
  sub_handle = []
  handle.each do |str|
    if str.match(/^@[A-Z]/)
      sub_handle << str
    end
  end
  puts "Il y a #{sub_handle.size} qui ont une majuscule en première lettre"
end

def contain_uppercase(handle)
  #combien de handle contiennent au moins une majuscule
  sub_handle = []
  handle.each do |str|
    #regex majuscule
    if str.match(/[A-Z]/)
      sub_handle << str
    end
  end
  puts " #{sub_handle.size} handle contiennent au moins une majuscule"
end

def count_underscore(handle)
#  sub_handle = []
  sum = 0
  handle.each do |str|
    #regex underscore
    if str.match(/_/)
#      sub_handle << str
#.scan(/_/) renvoie un tableau contenant chaque _ trouvé dans str.
#.size renvoie la taille de ce tableau
#donc le nombre de _ dans str
      sum = sum + str.scan(/_/).size
    end
  end
  puts "Il y a #{sum} underscores au total dans ce tableau"
end

def alphabetic_sort(handle)
  handle.sort
  return handle.sort
end

def fifty_shorter(handle)
#on fait un hash vide
  mon_hash = {}
#on stock nos values(taille du handle) et keys (handle) dedans
  handle.each do |str|
    mon_hash[str] = str.size
  end
#on trie notre hash avec .sort_by
  mon_hash = mon_hash.sort_by{|str, size| size}.to_h
#on le retourne avec les 50 premières clefs
  puts  "Les 50 handle les plus courts son : #{mon_hash.keys[0..49]}"
end

def position(handle)
  #on fait un hash vide
    mon_hash = {}
    position = 0
  #on stock nos values(taille du handle) et keys (handle) dedans
    handle.each do |str|
      mon_hash[str] = position
      position +=1
    end
    puts "Le handle @epenser se trouve à la #{mon_hash["@epenser"]}-ème position (en comptant à partir de 0)"
end

